import React from 'react';
import styles from './Card.module.css';
import { IoCloseCircleOutline } from "react-icons/io5";

export default function Card(props) {
const { max, min, name, img, onClose, primary } = props;
// aca va tu codigo
  return (  
    <div className={`${styles.card} ${primary ? styles.primary : ""}`}>  {/* Si la card es primary(primaria) aplica estos estilos, si no, no apliques nada. Se puede hacer con backstip o array.  */}
      <span className={styles.name}> 
        {name}
        { !primary && <button primary  className={styles.button} onClick={onClose}> {/*Solo si primary No existe, el boton se renderiza  */}
          <IoCloseCircleOutline/> {/*boton X de eliminar, lo agrego dentro del span(nombre de la ciudad) */}
        </button>} 
      </span> 
      <img 
        src={`http://openweathermap.org/img/wn/${img}@2x.png`} 
        alt="Icono del clima"/>   
      <div className={styles.temps}>
        <Temp Label="Min" temp={min} />
        <Temp Label="Max" temp={max} />  
      </div>
    </div>
  );
}

//Mala practica ALERT
//Esto deberia estar en un archivo aparte
function Temp({ Label, temp }) {
  return (
    <div className={styles.temp}>
      <span>{Label}</span>
      <span>{temp}º</span>
    </div>
  );
}


