import React, { useState } from "react";

export default function SearchBar({onSearch}) {
  const [city, setCity] = useState('') //en principio mi array sera vacio
  return (
    <form onSubmit={(e) => {
      e.preventDefault();
      onSearch(city);
    }}>
      <input
        type="text"
        placeholder="Ciudad..."
        value = {city}
        onChange={e => setCity(e.target.value)}
      />
      <input type="submit" value="Agregar" />
    </form>
  );
}

