import React, { useState } from 'react'; //Se agregó el { useState } traje mi hook para usar
import './App.css';
import Nav from './components/Nav.jsx';
import Cards from './components/Cards.jsx';


export default function App() {
  const [cities, setCities] = useState([]);

  var apiKey = '4ae2636d8dfbdc3044bede63951a019b' // -> Es confidencial, no usar una cte, si una variable de entorno
  
  //Variable de entorno para guardar mi apikey
  //const apiKey = process.env.REACT_APP_APIKEY; 

  function onSearch(ciudad) {
    //Acá habría que hacer el llamado a la API para obtener los datos de la ciudad(pegue la Api)
    fetch(`http://api.openweathermap.org/data/2.5/weather?q=${ciudad}&appid=${apiKey}&units=metric`)
      .then(r => r.json())
      .then((recurso) => {
        if(recurso.main !== undefined){
          const ciudad = { //Crea un objeto ciudad y lo agrega al estado cities
            min: Math.round(recurso.main.temp_min),
            max: Math.round(recurso.main.temp_max),
            img: recurso.weather[0].icon,
            id: recurso.id,
            wind: recurso.wind.speed,
            temp: recurso.main.temp,
            name: recurso.name,
            weather: recurso.weather[0].main,
            clouds: recurso.clouds.all,
            latitud: recurso.coord.lat,
            longitud: recurso.coord.lon
          };
          setCities(oldCities => [...oldCities, ciudad]);
        } else {
          alert("Ciudad no encontrada");
        }
      });
  
    }

    function onClose(id) {
      setCities(oldCities => oldCities.filter(c => c.id != id));
    }
  
  return ( //explicado en mi cuaderno
    <div className="App">
      <Nav onSearch={onSearch}/>
      <Cards cities={cities} onClose={onClose} />
    </div>
  );

}
