import React from 'react';
import estilos from './Card.module.css'

export default function Card(props) {
  // acá va tu código
  return (
    <div className={estilos.contenedor}> {/* empieza el div padre */}
      <button onClick={props.onClose} className={estilos.btn}>X</button> 
      <h4>{props.name}</h4>
    <div className={estilos.infoCont}>
      <div>
        <h6>Min</h6>
        <p>{props.min}</p> 
      </div>
      <div>
        <h6>Max</h6>
        <p>{props.max}</p>
      </div>
    <img src={`http://openweathermap.org/img/wn/${props.img}@2x.png`} />
  </div> 
  </div> //Aqui termina el div padre
  )

};
