import React from 'react';

import Card from './components/Card.jsx';
import Cards from './components/Cards.jsx';
import SearchBar from './components/SearchBar.jsx';
import data, { Cairns } from './data.js';

import styles from "./App.module.css" //Traigo la imagen de fondo


//const apiKey = '2f47152f1c107a8b4f0d7b058cb65e6d' -> Es info secreta NUNCA debe ir en el codigo. Mi app debe ser portable, si quiero cambiar mi apikey no debo tocar el codigo
  
//Pasos para usar de forma optima una apikey
  // 1-Creo una variable de entorno p/ guardar mi apikey y guardo en una cte.
    // 1.1-por definicion "process.env" + ".REACT_APP_" + "APIKEY"(el nombre q yo asigne)
  // 2-Creo un archivo ".env" a nivel local y ahi pego mi apikey.

  //ACLARACION: Por requisi de React, al crear una var de entorno del lado del cliente(navegador), 
  // se empieza con ".REACT_APP_" y luego va el nombre q desee. Es la unica forma q la info llegue al navegador del cliente
  const apiKey = process.env.REACT_APP_APIKEY; //Var de entorno SIEMPRE EN MAYUSCULA


function onSearch(ciudad) {
  //Acá habría que hacer el llamado a la API para obtener los datos de la ciudad(pegue la Api)
  fetch (
  `http://api.openweathermap.org/data/2.5/weather?q=${ciudad}&appid=${apiKey}&units=metric` //pego esta ruta q me a responder con algo q aun no se
  )
    .then(r => r.json())
    .then((recurso) => {
      if(recurso.main !== undefined){
        const ciudad = { //Crea un objeto ciudad y lo agrega al estado cities
          min: Math.round(recurso.main.temp_min),
          max: Math.round(recurso.main.temp_max),
          img: recurso.weather[0].icon,
          id: recurso.id,
          wind: recurso.wind.speed,
          temp: recurso.main.temp,
          name: recurso.name,
          weather: recurso.weather[0].main,
          clouds: recurso.clouds.all,
          latitud: recurso.coord.lat,
          longitud: recurso.coord.lon
        };
        //setCities(oldCities => [...oldCities, ciudad]);
      } else {
        alert("Ciudad no encontrada");
      }
    });  
  }


    
    
function App() {
  return (
    <div className={styles.app}> {/*imagen de fondo total*/}
      <div className={styles.bkg}/> {/*imagen q va en el medio (borrosa)*/}
      <div className={styles.container}>
        <div>
          <SearchBar onSearch={onSearch} /> {/*..o pasarle la ciudad directamente,  */}
          {/*<SearchBar onSearch={(ciudad) => onSearch(ciudad)} /> --> ..puedo hacer esto..*/} 
        </div>    
        <div className={styles.citiesContainer}>
          <Card
            primary /*Solo con mencionarla React asigna el valor true */
            max={Cairns.main.temp_max}
            min={Cairns.main.temp_min}
            name={Cairns.name}
            img={Cairns.weather[0].icon}
            onClose={() => alert(Cairns.name)}
          />
          <Cards cities={data} />   
        </div>
    </div>
    </div>
  );
}

export default App;
